import React, { useRef, useState } from "react";
import {
  Tab,
  CurrencyIcon,
  Counter,
} from "@ya.praktikum/react-developer-burger-ui-components";
import ingredientsStyles from "./burgerIngredients.module.css";

const IngridientItem = ({ ingridient }) => {
  return (
    <div className={ingredientsStyles.ingridient__item}>
      <img src={ingridient.image} alt="фото." />
      <div className={`${ingredientsStyles.ingridient__price} pt-1 pb-1`}>
        <p className="text text_type_digits-default pr-2">{ingridient.price}</p>
        <CurrencyIcon />
      </div>
      <p
        className={`${ingredientsStyles.text__align} text text_type_main-default`}
      >
        {ingridient.name}
      </p>
    </div>
  );
};

function BurgerIngredients({ ingridient }) {
  const [current, setCurrent] = useState("one");
  //нашла все булки
  const buns = ingridient.filter((m) => m.type === "bun");
  //нашла все соусы
  const sauces = ingridient.filter((m) => m.type === "sauce");
  //нашла все начинки
  const fillings = ingridient.filter((m) => m.type === "main");

  const ref = useRef();


  const skrollHandler = () => {
    if (current === "one" && current === "two" && current === "three") {
      ref.current.scrollIntoView({ behavior: "smooth" })
    } 
   ;
  }
  //в общем тебе надо сделать состояние,
  //где будет хранится выбранный таб и сравнивать его со значением таба
  return (
    <>
      <div style={{ display: "flex" }} className="pt-5 pb-5">
        <Tab value="one" active={current === "one"} onClick={skrollHandler}>
          Булки
        </Tab>
        <Tab value="two" active={current === "two"} onClick={skrollHandler}>
          Соусы
        </Tab>
        <Tab value="three" active={current === "three"} onClick={skrollHandler}>
          Начинки
        </Tab>
      </div>
      <div className={`${ingredientsStyles.ingridient__container} mt-5`}>
        <div className="pb-5">
          <h2 className="text text_type_main-medium pb-1" ref={ref}>
            Булки
          </h2>
          <ul className={`${ingredientsStyles.ingridient__list} pt-5`}>
            {buns.map((ingridient) => (
              <li key={ingridient._id}>
                <IngridientItem ingridient={ingridient} />
              </li>
            ))}
          </ul>
        </div>
        <div className="pt-5 pb-5">
          <h2 className="text text_type_main-medium pb-1" ref={ref}>
            Соусы
          </h2>
          <ul
            className={`${ingredientsStyles.ingridient__list} pt-5`}

          >
            {sauces.map((ingridient) => (
              <li key={ingridient._id}>
                <IngridientItem ingridient={ingridient} />
              </li>
            ))}
          </ul>
        </div>
        <div className="pt-5 pb-5">
          <h2 className="text text_type_main-medium pb-1" ref={ref}>
            Начинки
          </h2>
          <ul className={`${ingredientsStyles.ingridient__list} pt-5`} >
            {fillings.map((ingridient) => (
              <li key={ingridient._id}>
                <IngridientItem ingridient={ingridient} />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}

export default BurgerIngredients;
